    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        int f,venta;
        double im=0,ci=0,nl=0,i=0,nf=0;
        f=jComboBox1.getSelectedIndex();
        venta=Double.valueOf(jTextField1.getText()).intValue();
        
        switch(f)
        {
            case 0:
            {
                i=0.19*venta;
                im=0.05*venta;
                ci=0.6*venta;
                nl=2;
                nf=venta+i;
                break;
            }
            case 1:
            {
                i=0.19*venta;
                im=0.1*venta;
                ci=0.5*venta;
                nl=4;
                nf=venta+i;
                break;
            }
            case 2:
            {
                i=0.19*venta;
                im=0.15*venta;
                ci=0.35*venta;
                nl=6;
                nf=venta+i;
                break;
            }
        }
        jTextArea1.setText("");
        jTextArea1.append("impuesto:"+i+"\n");
        jTextArea1.append("Cuota Inicial:"+ci+"\n");
        jTextArea1.append("NÂ° de la letra:"+nl+"\n");
        jTextArea1.append("NuevaFactura:"+nf+"\n");
    }//GEN-LAST:event_jButton1ActionPerformed

