    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        double consumo,igv,total; //declaraci√≥n de variables
        consumo=Double.valueOf(jTextField1.getText()).doubleValue(); //Lectura de datos
        igv=0.18*consumo; //Proceso
        total =consumo+igv; //Proceso
        jTextArea1.setText(""); //Limpia
        jTextArea1.append("CONSUMO :"+consumo+"\n"); //salida
        jTextArea1.append("IGV :"+igv+"\n");
        jTextArea1.append("TOTAL :"+total+"\n");
    }//GEN-LAST:event_jButton1ActionPerformed

